import { useState } from "react";

const DR_LABELS = [
  { label: "No DR", color: "#2ecc71", desc: "Диабетическая ретинопатия не обнаружена" },
  { label: "Mild", color: "#a3d65c", desc: "Лёгкая стадия" },
  { label: "Moderate", color: "#f1c40f", desc: "Умеренная стадия" },
  { label: "Severe", color: "#e67e22", desc: "Тяжёлая стадия" },
  { label: "Proliferative DR", color: "#e74c3c", desc: "Критическая стадия" }
];

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const onFileChange = (e) => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f);
    setPreview(URL.createObjectURL(f));
    setResult(null);
  };

  const sendFile = async () => {
    if (!file) return alert("Выберите файл");
    setLoading(true);

    const form = new FormData();
    form.append("file", file);

    try {
      const res = await fetch("http://127.0.0.1:000/predict", {
        method: "POST",
        body: form
      });
      const data = await res.json();
      setResult(data);
    } catch (e) {
      alert("Ошибка отправки");
    } finally {
      setLoading(false);
    }
  };

  const pred = result ? DR_LABELS[result.class] : null;

  return (
    <div className="app">
      <h1>AI Health Predictor</h1>
      <p className="subtitle">Диагностика диабетической ретинопатии</p>

      <div className="card">
        <input type="file" accept="image/*" onChange={onFileChange} />
        {preview && <img src={preview} alt="preview" />}
        <button onClick={sendFile} disabled={loading}>
          {loading ? "Анализ..." : "Отправить на анализ"}
        </button>
      </div>

      {result && (
        <div className="result" style={{ borderColor: pred.color }}>
          <h2 style={{ color: pred.color }}>{pred.label}</h2>
          <p>{pred.desc}</p>

          <div className="prob">
            Вероятность: <strong>{(result.probability * 100).toFixed(2)}%</strong>
          </div>

          <ul>
            {result.top?.map((t, i) => (
              <li key={i}>
                {DR_LABELS[t.class].label}
                <span>{(t.probability * 100).toFixed(1)}%</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
export default App
