import { useState } from "react";

/* ===== Справочник стадий ===== */
const DR_MAP = {
  "No DR": {
    color: "#2ecc71",
    desc: "Диабетическая ретинопатия не обнаружена"
  },
  "Mild": {
    color: "#a3d65c",
    desc: "Лёгкая стадия"
  },
  "Moderate": {
    color: "#f1c40f",
    desc: "Умеренная стадия"
  },
  "Severe": {
    color: "#e67e22",
    desc: "Тяжёлая стадия"
  },
  "Proliferative DR": {
    color: "#e74c3c",
    desc: "Критическая стадия"
  }
};

/* ===== Утилита ===== */
const normalizeLabel = (label) => {
  if (!label || typeof label !== "string") return null;
  return label.trim();
};

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  /* ===== Выбор файла ===== */
  const onFileChange = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    setFile(f);
    setPreview(URL.createObjectURL(f));
    setResult(null);
  };

  /* ===== Отправка на backend ===== */
  const sendFile = async () => {
    if (!file) {
      alert("Выберите изображение");
      return;
    }

    setLoading(true);
    const form = new FormData();
    form.append("file", file);

    try {
      const res = await fetch("http://127.0.0.1:7000/predict", {
        method: "POST",
        body: form
      });

      const data = await res.json();
      setResult(data);
      console.log("BACKEND RESPONSE:", data);
    } catch (err) {
      alert("Ошибка соединения с сервером");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  /* ===== Разбор ответа ===== */
  const mainPred =
    result &&
    Array.isArray(result.top) &&
    result.top.length > 0
      ? result.top[0]
      : null;

  const label = mainPred ? normalizeLabel(mainPred.label) : null;

  const meta =
    label && DR_MAP[label]
      ? DR_MAP[label]
      : {
          color: "#3498db",
          desc: "Описание недоступно"
        };

  /* ===== UI ===== */
  return (
    <div className="app">
      <h1>AI Health Predictor</h1>
      <p className="subtitle">Диагностика диабетической ретинопатии</p>

      <div className="card">
        <input type="file" accept="image/*" onChange={onFileChange} />
        {preview && <img src={preview} alt="preview" />}
        <button onClick={sendFile} disabled={loading}>
          {loading ? "Анализ..." : "Отправить на анализ"}
        </button>
      </div>

      {mainPred && (
        <div
          className="result"
          style={{ borderLeft: `6px solid ${meta.color}` }}
        >
          <h2 style={{ color: meta.color }}>
            {label} — {(mainPred.probability * 100).toFixed(0)}%
          </h2>

          <p>{meta.desc}</p>

          <h4>Все вероятности:</h4>
          <ul>
            {result.top.map((t, i) => (
              <li key={i}>
                {normalizeLabel(t.label)}
                <span>
                  {(t.probability * 100).toFixed(1)}%
                </span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
