import React, { useState } from "react";

// Конфигурация стадий и цветов
const DR_STAGES = {
  "0": { title: "Нет ДР", color: "#4ade80", desc: "Сетчатка выглядит здоровой. Признаков патологии не найдено." },
  "1": { title: "Легкая", color: "#a3e635", desc: "Начальные изменения. Обнаружены единичные микроаневризмы." },
  "2": { title: "Умеренная", color: "#facc15", desc: "Заметные нарушения. Кровоизлияния средней степени." },
  "3": { title: "Тяжелая", color: "#fb923c", desc: "Серьезные повреждения сосудов. Высокий риск потери зрения." },
  "4": { title: "Пролиферативная", color: "#f87171", desc: "Критическая стадия. Рост новообразованных сосудов." }
};

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const onFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setPreview(URL.createObjectURL(selectedFile));
      setResult(null);
    }
  };

  const sendFile = async () => {
    if (!file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Server error");
      const data = await response.json();
      
      // Имитация задержки для красоты анимации (опционально)
      setTimeout(() => setResult(data), 500);
      
    } catch (error) {
      alert("Ошибка подключения к серверу (Порт 7000)");
      console.error(error);
    } finally {
      setTimeout(() => setLoading(false), 500);
    }
  };

  const getStageData = (label) => 
    DR_STAGES[label] || { title: label, color: "#94a3b8", desc: "Анализ завершен" };

  const mainResult = result?.top?.[0];
  const mainStage = mainResult ? getStageData(mainResult.label) : null;

  return (
    <div className="app-container">
      <div className="card">
        <header>
          <h1 className="app-title">RetinaAI Pro</h1>
          <p className="app-subtitle">Система диагностики диабетической ретинопатии</p>
        </header>

        <div className="content-grid">
          {/* Левая колонка: Загрузка */}
          <div className="upload-section">
            <label className="drop-zone">
              <input type="file" onChange={onFileChange} accept="image/*" hidden />
              {preview ? (
                <img src={preview} alt="Scan" className="preview-image" />
              ) : (
                <div className="placeholder-text">
                  <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" style={{marginBottom: '10px', opacity: 0.5}}>
                    <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                    <circle cx="8.5" cy="8.5" r="1.5"></circle>
                    <polyline points="21 15 16 10 5 21"></polyline>
                  </svg>
                  <p>Нажмите для загрузки снимка</p>
                </div>
              )}
            </label>

            <button 
              className="analyze-btn"
              onClick={sendFile} 
              disabled={!file || loading}
            >
              {loading ? <span className="loader"></span> : "Запустить анализ"}
            </button>
          </div>

          {/* Правая колонка: Результат (появляется только если есть result) */}
          {mainResult && (
            <div className="result-panel">
              <div className="diagnosis-header">
                <span className="confidence-badge">Вероятность: {(mainResult.probability * 100).toFixed(1)}%</span>
                <h2 
                  className="main-stage-title" 
                  style={{ color: mainStage.color, textShadow: `0 0 20px ${mainStage.color}40` }}
                >
                  {mainStage.title}
                </h2>
                <p className="stage-description">{mainStage.desc}</p>
              </div>

              <div className="stats-list">
                {result.top.map((item, idx) => {
                  const stage = getStageData(item.label);
                  const percent = (item.probability * 100).toFixed(1);
                  
                  return (
                    <div key={idx} className="stat-item">
                      <div className="stat-header">
                        <span>{stage.title}</span>
                        <span>{percent}%</span>
                      </div>
                      <div className="progress-track">
                        <div 
                          className="progress-fill" 
                          style={{ 
                            width: `${percent}%`, 
                            backgroundColor: stage.color,
                            boxShadow: `0 0 10px ${stage.color}80` 
                          }}
                        ></div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
