import { useState } from "react";

export default function App() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleSubmit = async () => {
    if (!image) return;

    setLoading(true);

    const formData = new FormData();
    formData.append("image", image);

    // ❗ тут подставь свой реальный API
    const response = await fetch("http://localhost:8000/predict", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    setResult(data);
    setLoading(false);
  };

  const getColor = (label) => {
    switch (label) {
      case "No DR":
        return "text-green-400";
      case "Mild":
        return "text-yellow-400";
      case "Moderate":
        return "text-orange-400";
      case "Severe":
        return "text-red-400";
      case "Proliferative":
        return "text-red-600";
      default:
        return "text-gray-300";
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white px-4 py-6">
      <div className="max-w-3xl mx-auto space-y-6">

        {/* Заголовок */}
        <h1 className="text-2xl md:text-3xl font-bold text-center">
          Diabetic Retinopathy Detection
        </h1>

        {/* Загрузка */}
        <div className="bg-gray-800 p-4 rounded-xl space-y-4">
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="w-full text-sm"
          />

          {preview && (
            <img
              src={preview}
              alt="preview"
              className="w-full max-h-64 object-contain rounded-lg"
            />
          )}

          <button
            onClick={handleSubmit}
            disabled={loading}
            className="w-full bg-blue-600 hover:bg-blue-700 transition rounded-lg py-2 font-semibold"
          >
            {loading ? "Analyzing..." : "Analyze"}
          </button>
        </div>

        {/* Результат */}
        {result && (
          <div className="bg-gray-800 p-5 rounded-xl space-y-4">

            {/* Главный диагноз */}
            <div className="text-center">
              <h2
                className={`text-3xl font-bold ${getColor(result.top[0].label)}`}
              >
                {result.top[0].label}
              </h2>
              <p className="text-gray-400 mt-1">
                Probability: {Math.round(result.top[0].probability * 100)}%
              </p>
            </div>

            {/* Все вероятности */}
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
              {result.top.map((item, index) => (
                <div
                  key={index}
                  className="flex justify-between bg-gray-700 px-4 py-2 rounded-lg"
                >
                  <span className={getColor(item.label)}>
                    {item.label}
                  </span>
                  <span>
                    {Math.round(item.probability * 100)}%
                  </span>
                </div>
              ))}
            </div>

          </div>
        )}
      </div>
    </div>
  );
}
