import React, { useState } from "react";
import "./App.css";

// ТОЧНЫЙ МАППИНГ: Цифры от бэкенда -> Твои названия
const DR_STAGES = {
  "0": { 
    title: "No DR", 
    color: "#4ade80", 
    desc: "Диабетическая ретинопатия не обнаружена. Сетчатка здорова." 
  },
  "1": { 
    title: "Mild", 
    color: "#a3e635", 
    desc: "Лёгкая стадия. Микроаневризмы." 
  },
  "2": { 
    title: "Moderate", 
    color: "#facc15", 
    desc: "Умеренная стадия. Кровоизлияния и экссудаты." 
  },
  "3": { 
    title: "Severe", 
    color: "#fb923c", 
    desc: "Тяжёлая стадия. Значительные нарушения сосудов." 
  },
  "4": { 
    title: "Proliferative DR", 
    color: "#f87171", 
    desc: "Пролиферативная стадия. Критическое состояние." 
  }
};

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const onFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setPreview(URL.createObjectURL(selectedFile));
      setResult(null);
    }
  };

  const sendFile = async () => {
    if (!file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);

    try {
      // Порт 7000 (твой бэкенд)
      const response = await fetch("http://127.0.0.1:ү000/predict", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Server error");
      const data = await response.json();
      
      // Небольшая задержка для плавности анимации UI
      setTimeout(() => setResult(data), 600);
      
    } catch (error) {
      alert("Ошибка: Не удалось соединиться с сервером на порту 7000");
      console.error(error);
    } finally {
      setTimeout(() => setLoading(false), 600);
    }
  };

  // Получаем данные стадии по метке (цифре или тексту)
  const getStageData = (label) => {
    // Сначала пробуем найти по точному совпадению (например "0")
    if (DR_STAGES[label]) return DR_STAGES[label];
    
    // Если вдруг придет название текстом ("No DR"), ищем объект, у которого title совпадает
    const foundKey = Object.keys(DR_STAGES).find(key => DR_STAGES[key].title === label);
    if (foundKey) return DR_STAGES[foundKey];

    // Фоллбэк, если ничего не нашлось
    return { title: label, color: "#94a3b8", desc: "Стадия определена" };
  };

  const mainResult = result?.top?.[0];
  const mainStage = mainResult ? getStageData(mainResult.label) : null;

  return (
    <div className="app-container">
      <div className="card">
        <header>
          <h1 className="app-title">RetinaAI Pro</h1>
          <p className="app-subtitle">Clinical Grade Analysis</p>
        </header>

        <div className="content-grid">
          {/* Левая колонка: Загрузка */}
          <div className="upload-section">
            <label className="drop-zone">
              <input type="file" onChange={onFileChange} accept="image/*" hidden />
              {preview ? (
                <img src={preview} alt="Scan" className="preview-image" />
              ) : (
                <div className="placeholder-text">
                  <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" style={{marginBottom: '10px', opacity: 0.5}}>
                    <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                    <circle cx="8.5" cy="8.5" r="1.5"></circle>
                    <polyline points="21 15 16 10 5 21"></polyline>
                  </svg>
                  <p>Загрузить изображение сетчатки</p>
                </div>
              )}
            </label>

            <button 
              className="analyze-btn"
              onClick={sendFile} 
              disabled={!file || loading}
            >
              {loading ? <span className="loader"></span> : "Запустить анализ"}
            </button>
          </div>

          {/* Правая колонка: Результат */}
          {mainResult && (
            <div className="result-panel">
              <div className="diagnosis-header">
                <span className="confidence-badge">Confidence: {(mainResult.probability * 100).toFixed(1)}%</span>
                <h2 
                  className="main-stage-title" 
                  style={{ color: mainStage.color, textShadow: `0 0 25px ${mainStage.color}50` }}
                >
                  {mainStage.title}
                </h2>
                <p className="stage-description">{mainStage.desc}</p>
              </div>

              <div className="stats-list">
                {result.top.map((item, idx) => {
                  const stage = getStageData(item.label); // Преобразуем "0" -> "No DR" для списка
                  const percent = (item.probability * 100).toFixed(1);
                  
                  return (
                    <div key={idx} className="stat-item">
                      <div className="stat-header">
                        <span>{stage.title}</span>
                        <span>{percent}%</span>
                      </div>
                      <div className="progress-track">
                        <div 
                          className="progress-fill" 
                          style={{ 
                            width: `${percent}%`, 
                            backgroundColor: stage.color,
                            boxShadow: `0 0 10px ${stage.color}80` 
                          }}
                        ></div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
