import { useState } from "react";
import "./App.css"; // Импортируем стили

/* ===== Справочник стадий ===== */
const DR_MAP = {
  "No DR": {
    color: "#4CAF50", // Более яркий зеленый
    desc: "Диабетическая ретинопатия не обнаружена",
    displayName: "Нет ДР",
  },
  "Mild": {
    color: "#8BC34A", // Светло-зеленый
    desc: "Лёгкая стадия",
    displayName: "Легкая",
  },
  "Moderate": {
    color: "#FFEB3B", // Желтый
    desc: "Умеренная стадия",
    displayName: "Умеренная",
  },
  "Severe": {
    color: "#FF9800", // Оранжевый
    desc: "Тяжёлая стадия",
    displayName: "Тяжелая",
  },
  "Proliferative DR": {
    color: "#F44336", // Красный
    desc: "Пролиферативная диабетическая ретинопатия",
    displayName: "Пролиферативная",
  },
};

/* ===== Утилита ===== */
const normalizeLabel = (label) => {
  if (!label || typeof label !== "string") return null;
  return label.trim();
};

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  /* ===== Выбор файла ===== */
  const onFileChange = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    setFile(f);
    setPreview(URL.createObjectURL(f));
    setResult(null); // Сбрасываем результаты при выборе нового файла
  };

  /* ===== Отправка на backend ===== */
  const sendFile = async () => {
    if (!file) {
      alert("Пожалуйста, выберите изображение.");
      return;
    }

    setLoading(true);
    const form = new FormData();
    form.append("file", file);

    try {
      const res = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        body: form,
      });

      if (!res.ok) {
        throw new Error(`Ошибка сервера: ${res.status} ${res.statusText}`);
      }

      const data = await res.json();
      setResult(data);
      console.log("BACKEND RESPONSE:", data);
    } catch (err) {
      alert(`Ошибка при отправке файла: ${err.message}`);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  /* ===== Разбор ответа ===== */
  const mainPred =
    result && Array.isArray(result.top) && result.top.length > 0
      ? result.top[0]
      : null;

  const label = mainPred ? normalizeLabel(mainPred.label) : null;

  const meta =
    label && DR_MAP[label]
      ? DR_MAP[label]
      : {
          color: "#9E9E9E", // Серый для неопределенных
          desc: "Описание недоступно",
          displayName: "Неизвестная стадия",
        };

  /* ===== UI ===== */
  return (
    <div className="app-container">
      <div className="card">
        <h1 className="app-title">AI Health Predictor</h1>
        <p className="app-subtitle">Диагностика диабетической ретинопатии</p>

        <div className="input-section">
          <label htmlFor="file-upload" className="custom-file-upload">
            {file ? file.name : "Выберите изображение"}
          </label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={onFileChange}
            className="hidden-input"
          />
          {preview && (
            <div className="image-preview">
              <img src={preview} alt="Предварительный просмотр" />
            </div>
          )}
          <button
            onClick={sendFile}
            disabled={loading || !file}
            className="analyze-button"
          >
            {loading ? (
              <>
                <span className="spinner"></span> Анализ...
              </>
            ) : (
              "Отправить на анализ"
            )}
          </button>
        </div>

        {mainPred && (
          <div
            className="result-section"
            style={{ borderLeftColor: meta.color }}
          >
            <h2 className="result-main-title" style={{ color: meta.color }}>
              {meta.displayName} — {(mainPred.probability * 100).toFixed(0)}%
            </h2>
            <p className="result-description">{meta.desc}</p>

            <h4 className="probabilities-title">Все вероятности:</h4>
            <ul className="probabilities-list">
              {result.top.map((t, i) => {
                const currentLabel = normalizeLabel(t.label);
                const currentMeta = DR_MAP[currentLabel] || {
                  displayName: currentLabel,
                  color: "#9E9E9E",
                };
                return (
                  <li key={i} className="probability-item">
                    <span
                      className="probability-label"
                      style={{ color: currentMeta.color }}
                    >
                      {currentMeta.displayName}
                    </span>
                    <span className="probability-value">
                      {(t.probability * 100).toFixed(1)}%
                    </span>
                  </li>
                );
              })}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
