import React, { useState } from "react";
import "./App.css";

// Маппинг индексов модели в красивые названия и описания
const DR_STAGES = {
  "0": { title: "Нет ДР", color: "#2ecc71", desc: "Признаков ретинопатии не обнаружено. Сетчатка здорова." },
  "1": { title: "Легкая стадия", color: "#a3d65c", desc: "Микроаневризмы (небольшие расширения сосудов сетчатки)." },
  "2": { title: "Умеренная стадия", color: "#f1c40f", desc: "Наличие кровоизлияний и поврежденных сосудов." },
  "3": { title: "Тяжелая стадия", color: "#e67e22", desc: "Множественные кровоизлияния, высокий риск прогрессирования." },
  "4": { title: "Пролиферативная", color: "#e74c3c", desc: "Критическая стадия. Рост новых аномальных сосудов." }
};

function App() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const onFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setPreview(URL.createObjectURL(selectedFile));
      setResult(null);
    }
  };

  const sendFile = async () => {
    if (!file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);

    try {
      // ПОРТ 7000 — как указано в твоем APP_PORT в бэкенде
      const response = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Ошибка сервера");

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Error:", error);
      alert("Не удалось соединиться с сервером (порт 7000)");
    } finally {
      setLoading(false);
    }
  };

  // Получаем данные для отрисовки на основе лейбла (цифры или текста)
  const getStageData = (label) => {
    return DR_STAGES[label] || { title: label, color: "#3498db", desc: "Стадия определена" };
  };

  const mainResult = result?.top?.[0];
  const mainStage = mainResult ? getStageData(mainResult.label) : null;

  return (
    <div className="app-container">
      <div className="card">
        <header>
          <h1 className="app-title">AI Vision Health</h1>
          <p className="app-subtitle">Анализ диабетической ретинопатии по снимку</p>
        </header>

        <div className="upload-zone">
          <label className="file-input-label">
            <input type="file" onChange={onFileChange} accept="image/*" hidden />
            <div className="upload-button-styled">
              {file ? "Изменить файл" : "Выбрать снимок"}
            </div>
          </label>
          
          {preview && (
            <div className="preview-container">
              <img src={preview} alt="Снимок сетчатки" className="main-preview" />
            </div>
          )}

          <button 
            className={`analyze-btn ${loading ? 'loading' : ''}`}
            onClick={sendFile} 
            disabled={!file || loading}
          >
            {loading ? <div className="loader"></div> : "Начать диагностику"}
          </button>
        </div>

        {mainResult && (
          <div className="result-card" style={{ borderTop: `4px solid ${mainStage.color}` }}>
            <div className="result-header">
              <span className="status-dot" style={{ backgroundColor: mainStage.color }}></span>
              <h2 style={{ color: mainStage.color }}>{mainStage.title}</h2>
              <span className="percentage">{(mainResult.probability * 100).toFixed(1)}%</span>
            </div>
            <p className="stage-desc">{mainStage.desc}</p>

            <div className="all-predictions">
              <h4>Вероятности всех стадий:</h4>
              {result.top.map((item, idx) => {
                const stage = getStageData(item.label);
                return (
                  <div key={idx} className="progress-row">
                    <span className="row-label">{stage.title}</span>
                    <div className="progress-bar-bg">
                      <div 
                        className="progress-bar-fill" 
                        style={{ width: `${item.probability * 100}%`, backgroundColor: stage.color }}
                      ></div>
                    </div>
                    <span className="row-value">{(item.probability * 100).toFixed(1)}%</span>
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
